contracts/Deploy.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/metatx/MinimalForwarder.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "@openzeppelin/contracts/utils/Create2.sol";

import "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";

import "./modules/RecoveryManager.sol";
import "./guards/TwoFactorGuard.sol";
import "./scripts/InitializationScriptV1.sol";
import "./Factory.sol";

contract Deploy is Ownable {
    MinimalForwarder public forwarder;
    RecoveryManager public singeltonRecoveryManager;

    GnosisSafe public gnosisSingleton;
    InitializationScriptInterface public initScript;

    Factory public factory;

    function step1(bytes calldata minimalForwarderCreationBytecode_, bytes calldata recoveryManagerCreationBytecode_) public onlyOwner {
        address _forwarder = Create2.deploy(0, keccak256("MinimalForwarder"), minimalForwarderCreationBytecode_);

        address _singeltonRecoveryManager = Create2.deploy(
            0,
            keccak256("RecoveryManager"),
            abi.encodePacked(recoveryManagerCreationBytecode_, abi.encode(_forwarder))
        );

        forwarder = MinimalForwarder(_forwarder);
        singeltonRecoveryManager = RecoveryManager(_singeltonRecoveryManager);
    }

    function step2(bytes calldata gnosisSafeCreationBytecode_, bytes calldata initScriptCreationBytecode_) public onlyOwner {
        require(address(forwarder) != address(0x00), "Forwarder is not deployed");
        require(address(singeltonRecoveryManager) != address(0x00), "RecoveryManager is not deployed");

        gnosisSingleton = GnosisSafe(payable(Create2.deploy(0, keccak256("GnosisSafe"), gnosisSafeCreationBytecode_)));
        initScript = InitializationScriptV1(Create2.deploy(0, keccak256("InitializationScriptV1"), initScriptCreationBytecode_));
    }

    function step3(bytes calldata factoryCreationBytecode_, address deployer, address verifyer) public onlyOwner {
        require(address(gnosisSingleton) != address(0x00), "Singleton is not deployed");
        require(address(initScript) != address(0x00), "InitScript is not deployed");
        require(address(factory) == address(0x00), "Factory is already deployed");

        address _singeltonFactory = Create2.deploy(0, keccak256("Factory"), factoryCreationBytecode_);

        Factory _factory = Factory(
            address(
                new ERC1967Proxy(
                    address(_singeltonFactory),
                    abi.encodeWithSelector(Factory.initialize.selector, initScript, gnosisSingleton, singeltonRecoveryManager, verifyer)
                )
            )
        );

        _factory.addDeployer(deployer);
        factory = _factory;
    }
}

contracts/Factory.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/EIP712Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/utils/Create2.sol";

import "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";
import "@gnosis.pm/safe-contracts/contracts/common/Enum.sol";
import "@gnosis.pm/safe-contracts/contracts/proxies/GnosisSafeProxy.sol";

import "./modules/RecoveryManager.sol";
import "./guards/TwoFactorGuard.sol";
import "./scripts/InitializationScriptInterface.sol";
import "./scripts/InitializationScriptV1.sol";

contract Factory is Initializable, UUPSUpgradeable, OwnableUpgradeable, EIP712Upgradeable {
    struct StorageV1 {
        address twoFactorVerifier;
        TwoFactorGuard twoFactorGuard;
        InitializationScriptInterface initScript;
        GnosisSafe singleton;
        RecoveryManager singletonRecoveryManager;
        mapping(address => bool) deployers;
    }

    event AccountCreated(address indexed authKey, address account, address deviceKey, RecoveryManager recoveryManager);

    bytes32 internal constant _STORAGE_SLOT = bytes32(uint256(keccak256(abi.encodePacked("pluser.factory.storage.v1"))) - 1);

    bytes32 private constant _CREATE_ACCOUNT_TYPEHASH = keccak256("CreateAccount(address authKey,address deviceKey)");

    modifier onlyDeployer() {
        require(_getStorage().deployers[msg.sender], "Not a deployer");
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        InitializationScriptV1 initScript_,
        GnosisSafe singleton_,
        RecoveryManager singletonRecoveryManager_,
        address twoFactorVerifier_
    ) external initializer {
        __Ownable_init();
        __EIP712_init("PluserFactory", "1");

        StorageV1 storage store = _getStorage();

        store.twoFactorGuard = new TwoFactorGuard(Factory(address(this)));
        store.initScript = initScript_;
        store.singleton = singleton_;
        store.singletonRecoveryManager = singletonRecoveryManager_;
        store.twoFactorVerifier = twoFactorVerifier_;
    }

    // ------ VIEW ------

    function getTwoFactorGuard() external view returns (TwoFactorGuard) {
        return _getStorage().twoFactorGuard;
    }

    function getInitScript() external view returns (InitializationScriptInterface) {
        return _getStorage().initScript;
    }

    function getTwoFactorVerifier() external view returns (address) {
        return _getStorage().twoFactorVerifier;
    }

    function isDeployer(address deployer) external view returns (bool) {
        return _getStorage().deployers[deployer];
    }

    // ------ MUTABLE ------

    function addDeployer(address deployer) external onlyOwner {
        _getStorage().deployers[deployer] = true;
    }

    function removeDeployer(address deployer) external onlyOwner {
        _getStorage().deployers[deployer] = false;
    }

    function setTwoFactorVerifier(address twoFactorVerifier_) external onlyOwner {
        _getStorage().twoFactorVerifier = twoFactorVerifier_;
    }

    function deploy(address authKey, address deviceKey, bytes memory signature) external onlyDeployer returns (GnosisSafe wallet) {
        StorageV1 storage store = _getStorage();

        bytes32 structHash = keccak256(abi.encode(_CREATE_ACCOUNT_TYPEHASH, authKey, deviceKey));

        require(ECDSA.recover(_hashTypedDataV4(structHash), signature) == authKey, "Invalid signature (authKey)");

        wallet = GnosisSafe(
            payable(
                Create2.deploy(
                    0,
                    keccak256(abi.encodePacked(authKey)),
                    abi.encodePacked(type(GnosisSafeProxy).creationCode, uint256(uint160(address(store.singleton))))
                )
            )
        );

        address[] memory owners = new address[](1);
        owners[0] = deviceKey;

        bytes32 saltRecoveryManager = keccak256(abi.encodePacked(wallet, authKey, store.twoFactorGuard));
        RecoveryManager recoveryManager = RecoveryManager(
            Clones.cloneDeterministic(address(store.singletonRecoveryManager), saltRecoveryManager)
        );
        recoveryManager.initialize(wallet, authKey);

        emit AccountCreated(authKey, address(wallet), deviceKey, recoveryManager);

        // TODO: move numbers to constants
        wallet.setup(
            owners,
            1,
            address(store.initScript),
            abi.encodeCall(InitializationScriptInterface.initializeAccount, (recoveryManager, store.twoFactorGuard)),
            address(0),
            address(0),
            0,
            payable(address(0))
        );
    }

    // solhint-disable-next-line no-empty-blocks
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    //slither-disable-next-line assembly
    function _getStorage() private pure returns (StorageV1 storage store) {
        bytes32 position = _STORAGE_SLOT;
        assembly {
            store.slot := position
        }
    }
}

contracts/guards/TwoFactorGuard.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import { Guard } from "@gnosis.pm/safe-contracts/contracts/base/GuardManager.sol";
import "@gnosis.pm/safe-contracts/contracts/common/Enum.sol";
import "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";
import "@gnosis.pm/safe-contracts/contracts/common/SignatureDecoder.sol";
import "../Factory.sol";

contract TwoFactorGuard is Guard, SignatureDecoder {
    Factory public immutable factory;

    constructor(Factory factory_) {
        factory = factory_;
    }

    function checkTransaction(
        address to,
        uint256 value,
        bytes memory data,
        Enum.Operation operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address payable refundReceiver,
        bytes memory signatures,
        address /* msgSender */
    ) external view {
        require(operation == Enum.Operation.Call, "TwoFactorGuard: Only calls are allowed");

        GnosisSafe wallet = GnosisSafe(payable(msg.sender));

        uint256 safeNonce = wallet.nonce() - 1;
        bytes32 txHash = wallet.getTransactionHash(
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver,
            safeNonce
        );

        address verifyer = factory.getTwoFactorVerifier();

        uint256 walletThreshold = wallet.getThreshold();
        (uint8 v, bytes32 r, bytes32 s) = signatureSplit(signatures, walletThreshold);

        require(verifyer == ECDSA.recover(txHash, v, r, s), "TwoFactorGuard: Invalid signature");
    }

    function checkAfterExecution(bytes32 /*txHash*/, bool /*success*/) external pure {
        return;
    }
}

