pragma solidity 0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/metatx/MinimalForwarder.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "@openzeppelin/contracts/utils/Create2.sol";

import "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";

import "./modules/PluserModule.sol";
import "./scripts/InitializationScriptV1.sol";
import "./Factory.sol";

contract DeployHelper is Ownable {
    PluserModule public singeltonPluserModule;
    InitializationScriptInterface public initScript;

    Factory public factory;

    function deployPluserModule(bytes calldata pluserModuleCreationBytecode_, bytes calldata initScriptCreationBytecode_) public onlyOwner {
        address _singeltonPluserModule = Create2.deploy(0, keccak256("PluserModule"), abi.encodePacked(pluserModuleCreationBytecode_));

        singeltonPluserModule = PluserModule(_singeltonPluserModule);
        initScript = InitializationScriptV1(Create2.deploy(0, keccak256("InitializationScriptV1"), initScriptCreationBytecode_));
    }

    function deployFactory(
        bytes calldata factoryCreationBytecode_,
        address gnosisSingleton,
        address deployer,
        address verifyer
    ) public onlyOwner {
        require(address(singeltonPluserModule) != address(0x00), "Pluser Module is not deployed");
        require(address(factory) == address(0x00), "Factory is already deployed");

        address _singeltonFactory = Create2.deploy(0, keccak256("Factory"), factoryCreationBytecode_);
        Factory _factory = Factory(
            address(
                new ERC1967Proxy(
                    address(_singeltonFactory),
                    abi.encodeWithSelector(Factory.initialize.selector, initScript, gnosisSingleton, singeltonPluserModule, verifyer)
                )
            )
        );

        factory = _factory;
        _factory.addDeployer(deployer);
    }
}
","keccak256":"0xe7e4a6a7432bb647cd55ff008993b88347c93defcce03379e602ea8d4aac4506","license":"UNLICENSED"},"contracts/Factory.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/EIP712Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/utils/Create2.sol";

import "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";
import "@gnosis.pm/safe-contracts/contracts/common/Enum.sol";
import "@gnosis.pm/safe-contracts/contracts/proxies/GnosisSafeProxy.sol";

import "./modules/PluserModule.sol";
import "./scripts/InitializationScriptInterface.sol";
import "./scripts/InitializationScriptV1.sol";

contract Factory is Initializable, UUPSUpgradeable, OwnableUpgradeable, EIP712Upgradeable {
    struct StorageV1 {
        address twoFactorVerifier;
        InitializationScriptInterface initScript;
        GnosisSafe singleton;
        PluserModule singletonPluserModule;
        mapping(address => bool) deployers;
    }

    event AccountCreated(address indexed authKey, address account, address sessionKey, PluserModule pluserModule);

    bytes32 internal constant _STORAGE_SLOT = bytes32(uint256(keccak256(abi.encodePacked("pluser.factory.storage.v1"))) - 1);
    bytes32 internal constant _CREATE_ACCOUNT_TYPEHASH = keccak256("CreateAccount(address authKey,address sessionKey)");

    modifier onlyDeployer() {
        require(_getStorage().deployers[msg.sender], "Not a deployer");
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        InitializationScriptV1 initScript_,
        GnosisSafe singleton_,
        PluserModule singletonPluserModule_,
        address twoFactorVerifier_
    ) external initializer {
        __Ownable_init();
        __EIP712_init("PluserFactory", "1.0.0");

        StorageV1 storage store = _getStorage();

        store.initScript = initScript_;
        store.singleton = singleton_;
        store.singletonPluserModule = singletonPluserModule_;
        store.twoFactorVerifier = twoFactorVerifier_;
    }

    // ------ VIEW ------

    function getInitScript() external view returns (InitializationScriptInterface) {
        return _getStorage().initScript;
    }

    function getTwoFactorVerifier() external view returns (address) {
        return _getStorage().twoFactorVerifier;
    }

    function isDeployer(address deployer) public view returns (bool) {
        return _getStorage().deployers[deployer];
    }

    // ------ MUTABLE ------

    function addDeployer(address deployer) external onlyOwner {
        _getStorage().deployers[deployer] = true;
    }

    function removeDeployer(address deployer) external onlyOwner {
        _getStorage().deployers[deployer] = false;
    }

    function setTwoFactorVerifier(address twoFactorVerifier_) external onlyOwner {
        _getStorage().twoFactorVerifier = twoFactorVerifier_;
    }

    function deploy(address authKey, address sessionKey, bytes calldata signature, bytes calldata deployerSignature) external returns (GnosisSafe account) {
        StorageV1 storage store = _getStorage();

        // verify signatures
        bytes32 structHash = keccak256(abi.encode(_CREATE_ACCOUNT_TYPEHASH, authKey, sessionKey));
        require(ECDSA.recover(_hashTypedDataV4(structHash), signature) == authKey, "Invalid signature (authKey)");
        require(isDeployer(ECDSA.recover(_hashTypedDataV4(structHash), deployerSignature)), "Invalid signature (deployer)");

        // deploy account
        account = GnosisSafe(
            payable(
                Create2.deploy(
                    0,
                    keccak256(abi.encodePacked(authKey)),
                    abi.encodePacked(type(GnosisSafeProxy).creationCode, uint256(uint160(address(store.singleton))))
                )
            )
        );

        // deploy PluserModule
        address[] memory owners = new address[](1);
        owners[0] = sessionKey;

        PluserModule pluserModule = PluserModule(
            Clones.cloneDeterministic(address(store.singletonPluserModule), keccak256(abi.encodePacked(account)))
        );

        pluserModule.initialize(account, authKey, sessionKey, this);

        emit AccountCreated(authKey, address(account), sessionKey, pluserModule);

        // setup owners in account

        // TODO: move numbers to constants
        account.setup(
            owners,
            1,
            address(store.initScript),
            abi.encodeCall(InitializationScriptInterface.initializeAccount, (pluserModule)),
            address(0),
            address(0),
            0,
            payable(address(0))
        );
    }

    // solhint-disable-next-line no-empty-blocks
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    //slither-disable-next-line assembly
    function _getStorage() private pure returns (StorageV1 storage store) {
        bytes32 position = _STORAGE_SLOT;
        assembly {
            store.slot := position
        }
    }
}
","keccak256":"0x37ba11916b566ccd8550ed7ef5b039ec47bd62be559dbd92be37936c5930bd07","license":"UNLICENSED"},"contracts/libs/RecoveryRequest.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

library RecoveryRequest {
    struct NewSessionKey {
        address key;
        uint256 unlockTime;
    }

    function isUnlocked(NewSessionKey storage self) internal view returns (bool) {
        // slither-disable-next-line timestamp
        return block.timestamp > self.unlockTime;
    }

    function isExist(NewSessionKey storage self) internal view returns (bool) {
        return self.unlockTime != 0 && self.key != address(0x00);
    }

    function reset(NewSessionKey storage self) internal {
        self.unlockTime = 0;
        self.key = address(0x00);
    }
}
","keccak256":"0x6210620bfa723623322c3e87660f7c7998094960c3a3a15f9f3a1184769d9ee8","license":"UNLICENSED"},"contracts/modules/PluserModule.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import { Guard } from "@gnosis.pm/safe-contracts/contracts/base/GuardManager.sol";
import "@gnosis.pm/safe-contracts/contracts/common/Enum.sol";
import "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";
import "@gnosis.pm/safe-contracts/contracts/common/SignatureDecoder.sol";

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/EIP712Upgradeable.sol";

import "../libs/RecoveryRequest.sol";
import "../Factory.sol";

contract PluserModuleState {
    // --------------- Constants ---------------
    uint256 public constant SESSION_LIFETIME = 1 hours;
    uint256 public constant RECOVERY_TIME = 3 days;
    uint256 public constant SIGNATURE_LIFETIME = 15 minutes;

    // --------------- Typehashes ---------------
    bytes32 internal constant _CREATE_RECOVERY_REQUEST_TYPEHASH =
        keccak256("CreateRecoveryRequest(address newSessionKey,uint256 nonce,uint256 signTimestamp)");
    bytes32 internal constant _CANCEL_RECOVERY_TYPEHASH = keccak256("CancelRecovery(uint256 nonce,uint256 signTimestamp)");
    bytes32 internal constant _EXECUTE_RECOVERY_REQUEST_TYPEHASH = keccak256("ExecuteRecoveryRequest(uint256 nonce,uint256 signTimestamp)");
    bytes32 internal constant _UPDATE_SESSION_KEY_TYPEHASH =
        keccak256("UpdateSessionKey(address sessionKey,address newSessionKey,uint256 signTimestamp)");

    // --------------- Events ---------------
    event RequestCreated(address newSessionKey);
    event RequestCanceled(address sessionKey);
    event Restored(address newSessionKey);

    // --------------- Global vars ---------------
    GnosisSafe public account;
    address public authKey;
    Factory public factory;

    mapping(address => uint) public timeoutBySessionKey;

    uint256 public recoveryNonce;
    RecoveryRequest.NewSessionKey public recoveryRequest;
}

contract PluserModuleInternal is SignatureDecoder, PluserModuleState {
    function _verifySignature(bytes32 dataHash, uint8 v, bytes32 r, bytes32 s) internal pure returns (address currentAccount) {
        if (v > 30) {
            currentAccount = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", dataHash)), v - 4, r, s);
        } else {
            currentAccount = ecrecover(dataHash, v, r, s);
        }

        require(currentAccount != address(0), "PluserModule: Invalid signature");

        return currentAccount;
    }
}

contract PluserModule is PluserModuleInternal, EIP712Upgradeable, Guard {
    using RecoveryRequest for RecoveryRequest.NewSessionKey;

    // --------------- Modifiers ---------------
    modifier onlyAccount() {
        require(msg.sender == address(account), "PluserModule: Only account");
        _;
    }

    // --------------- Initialize ---------------
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(GnosisSafe account_, address authKey_, address sessionKey_, Factory factory_) external initializer {
        __EIP712_init("PluserModule", "0.0.369");

        account = account_;
        authKey = authKey_;
        factory = factory_;

        timeoutBySessionKey[sessionKey_] = block.timestamp + SESSION_LIFETIME;
    }

    // --------------- View functions ---------------
    function checkTransaction(
        address to,
        uint256 value,
        bytes calldata data,
        Enum.Operation operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address payable refundReceiver,
        bytes memory signatures,
        address /* msgSender */
    ) external view {
        require(operation == Enum.Operation.Call, "TwoFactorGuard: Only calls are allowed");

        require(msg.sender == address(account), "TwoFactorGuard: Invalid sender"); //TODO: msg.sender or  address /* msgSender */?

        bytes32 txHash = account.getTransactionHash(
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver,
            account.nonce() - 1
        );

        address signatureOwner;
        {
            (uint8 v, bytes32 r, bytes32 s) = signatureSplit(signatures, 0);
            signatureOwner = _verifySignature(txHash, v, r, s);
        }

        if (to == address(this)) {
            bytes4 selector = bytes4(data[0:4]);
            require(selector == PluserModule.updateSession.selector, "PluserModule: Invalid selector");
            address sessionKey = abi.decode(data[16:36], (address));
            require(sessionKey == signatureOwner, "PluserModule: Invalid session key signature");
        } else if (to == address(account)) {
            //TODO: blacklist
        } else {
            require(timeoutBySessionKey[signatureOwner] > block.timestamp, "PluserModule: Session key is expired");
        }
    }

    function checkAfterExecution(bytes32 /*txHash*/, bool /*success*/) external pure {
        return;
    }

    // --------------- Public mutable functions ---------------
    function createRecoveryRequest(
        address newSessionKey,
        bytes calldata authKeySignature,
        bytes calldata verifyerSignature,
        uint signTimestamp
    ) external {
        require(!recoveryRequest.isExist(), "RequestManager: Request already exists");
        require(!account.isOwner(newSessionKey), "RequestManager: account already exists");
        require(signTimestamp + SIGNATURE_LIFETIME > block.timestamp, "RequestManager: Signature is expired");

        bytes32 structHash = keccak256(abi.encode(_CREATE_RECOVERY_REQUEST_TYPEHASH, newSessionKey, recoveryNonce++, signTimestamp));
        require(ECDSA.recover(_hashTypedDataV4(structHash), authKeySignature) == authKey, "Invalid signature (authKey)");
        require(
            ECDSA.recover(_hashTypedDataV4(structHash), verifyerSignature) == factory.getTwoFactorVerifier(),
            "Invalid signature (verifyer)"
        );

        // slither-disable-next-line timestamp
        recoveryRequest = RecoveryRequest.NewSessionKey({ key: newSessionKey, unlockTime: block.timestamp + RECOVERY_TIME });
        emit RequestCreated(newSessionKey);
    }

    function cancelRecovery(
        address sessionKey,
        bytes calldata sessionKeySignature,
        bytes calldata verifyerSignature,
        uint signTimestamp
    ) external {
        require(recoveryRequest.isExist(), "RequestManager: Request not exists");
        require(account.isOwner(sessionKey), "RequestManager: Session key not exists");
        require(signTimestamp + SIGNATURE_LIFETIME > block.timestamp, "RequestManager: Signature is expired");

        bytes32 structHash = keccak256(abi.encode(_CANCEL_RECOVERY_TYPEHASH, recoveryNonce++, signTimestamp));
        require(ECDSA.recover(_hashTypedDataV4(structHash), sessionKeySignature) == sessionKey, "Invalid signature (session key)");
        require(
            ECDSA.recover(_hashTypedDataV4(structHash), verifyerSignature) == factory.getTwoFactorVerifier(),
            "Invalid signature (verifyer)"
        );

        emit RequestCanceled(recoveryRequest.key);

        recoveryRequest.reset();
    }

    function executeRecoveryRequest(bytes calldata authKeySignature, bytes calldata verifyerSignature, uint signTimestamp) external {
        require(recoveryRequest.isExist(), "RequestManager: Request not exists");
        require(recoveryRequest.isUnlocked(), "RequestManager: Request not unlocked");
        require(signTimestamp + SIGNATURE_LIFETIME > block.timestamp, "RequestManager: Signature is expired");

        bytes32 structHash = keccak256(abi.encode(_EXECUTE_RECOVERY_REQUEST_TYPEHASH, recoveryNonce++, signTimestamp));
        require(ECDSA.recover(_hashTypedDataV4(structHash), authKeySignature) == authKey, "Invalid signature (auth key)");
        require(
            ECDSA.recover(_hashTypedDataV4(structHash), verifyerSignature) == factory.getTwoFactorVerifier(),
            "Invalid signature (verifyer)"
        );

        address[] memory sessionKeys = account.getOwners();
        for (uint256 i = 0; i < sessionKeys.length; i++) {
            delete timeoutBySessionKey[sessionKeys[i]];

            address sessionKey = sessionKeys[i];
            address prevSessionKey = address(0x1);
            if (i != 0) {
                prevSessionKey = sessionKeys[i - 1];
            }

            require(
                account.execTransactionFromModule(
                    address(account),
                    0,
                    abi.encodeCall(account.removeOwner, (prevSessionKey, sessionKey, 1)),
                    Enum.Operation.Call
                ),
                "RequestManager: Remove owner failed"
            );
        }

        address newSessionKey = recoveryRequest.key;
        require(
            account.execTransactionFromModule(
                address(account),
                0,
                abi.encodeCall(account.addOwnerWithThreshold, (newSessionKey, 1)),
                Enum.Operation.Call
            ),
            "RequestManager: Add owner failed"
        );

        timeoutBySessionKey[newSessionKey] = block.timestamp + SESSION_LIFETIME;

        emit Restored(newSessionKey);
        recoveryRequest.reset();
    }

    function updateSession(
        address currentSessionKey,
        address newSessionKey,
        bytes calldata verifyerSignature,
        uint signTimestamp
    ) external onlyAccount {
        require(signTimestamp + SIGNATURE_LIFETIME > block.timestamp, "RequestManager: Signature is expired");
        require(account.isOwner(currentSessionKey), "PluserModule: Invalid session key");

        bytes32 structHash = keccak256(abi.encode(_UPDATE_SESSION_KEY_TYPEHASH, currentSessionKey, newSessionKey, signTimestamp));
        require(
            ECDSA.recover(_hashTypedDataV4(structHash), verifyerSignature) == factory.getTwoFactorVerifier(),
            "Invalid signature (verifyer)"
        );

        timeoutBySessionKey[newSessionKey] = block.timestamp + SESSION_LIFETIME;
        delete timeoutBySessionKey[currentSessionKey];

        address[] memory owners = account.getOwners();

        address prevOwner;
        for (uint256 i = 0; i < owners.length; i++) {
            if (owners[i] == currentSessionKey) {
                if (i == 0) {
                    prevOwner = address(0x1);
                } else {
                    prevOwner = owners[i - 1];
                }
            }
        }
        require(prevOwner != address(0x0), "PluserModule: Invalid session key");

        require(
            account.execTransactionFromModule(
                address(account),
                0,
                abi.encodeCall(account.removeOwner, (prevOwner, currentSessionKey, 1)),
                Enum.Operation.Call
            ),
            "PluserModule: Remove account failed"
        );

        require(
            account.execTransactionFromModule(
                address(account),
                0,
                abi.encodeCall(account.addOwnerWithThreshold, (newSessionKey, 1)),
                Enum.Operation.Call
            ),
            "PluserModule: Add account failed"
        );
    }
}
